package ArrayTest_2D;

public class NegativeSeperate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[]= {1,2,-3,8,4,-7};
		
		for(int i=0;i<arr.length;i++) {
			if(arr[i]>=0) {
				System.out.print(arr[i]);
			}
			if(arr[i]<0) {
				System.out.print(arr[i]);
			}
		}

	}

}
