package Loop_Test3;

public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int count=0;
         for(int i=1;i<=5;i++) {
        	 count++;
        	 for(int j=1;j<=i;j++) {
        		 System.out.print(count);
        	
        	  }
        	 System.out.print("\n");
	}

}
	}
