package com.day02;

public class MathExpression {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(5*2/3*(6+3/2));  //21 
		System.out.println((7-2+3*2)/(7-7+4*7)); //0
		System.out.println(4+2*3/2*4);//16
		System.out.println(18+2/5*4+45);//63

	}

}
