// 2 5 10 17 26.....      1*1+1=2
                      // 2*2+1=5
                       // 3*3+1=10
package com.day10;

public class Series1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mul;
		for(int i=1;i<=10;i++) {
			mul=i*i+1;
			System.out.print(mul+" ");
		}

	}

}
